'use strict';

var SameValueNonNumeric = require('es-abstract/2022/SameValueNonNumeric');
var Type = require('es-abstract/2022/Type');
var BigIntEqual = require('es-abstract/2022/BigInt/equal');
var NumberEqual = require('es-abstract/2022/Number/equal');

// https://262.ecma-international.org/13.0/#sec-isstrictlyequal

module.exports = function IsStrictlyEqual(x, y) {
	var xType = Type(x);
	var yType = Type(y);
	if (xType !== yType) {
		return false;
	}
	if (xType === 'Number' || xType === 'BigInt') {
		return xType === 'Number' ? NumberEqual(x, y) : BigIntEqual(x, y);
	}
	return SameValueNonNumeric(x, y);
};
