'use strict';

var SameValueNonNumber = require('es-abstract/2023/SameValueNonNumber');
var Type = require('es-abstract/2023/Type');
var NumberEqual = require('es-abstract/2023/Number/equal');

// https://262.ecma-international.org/14.0/#sec-isstrictlyequal

module.exports = function IsStrictlyEqual(x, y) {
	var xType = Type(x);
	var yType = Type(y);
	if (xType !== yType) {
		return false;
	}
	return xType === 'Number' ? NumberEqual(x, y) : SameValueNonNumber(x, y);
};
