'use strict';

var isFinite = require('es-abstract/helpers/isFinite');

var modulo = require('es-abstract/2023/modulo');
var ToNumber = require('es-abstract/2023/ToNumber');
var truncate = require('es-abstract/2023/truncate');

// https://262.ecma-international.org/14.0/#sec-touint8

module.exports = function ToUint8(argument) {
	var number = ToNumber(argument);
	if (!isFinite(number) || number === 0) {
		return 0;
	}
	var int = truncate(number);
	var int8bit = modulo(int, 0x100);
	return int8bit;
};
